<style>
  
  .text {
    color:white;
    text-align:center;
  }
  
  .title{
    margin-bottom:25px;
  }
  .container{
    background-color:black;
    background: rgba(0, 0, 0, 0.5);
    padding-top:35px;
    padding-bottom:35px;
    margin-top:75px;
    display:flex;
    justify-content:center;
  }
  btn-primay{
    color:grey;
    
  }

  :root {
    --red: #ef233c;
    --darkred: #c00424;
    --platinum: #e5e5e5;
    --black: #2b2d42;
    --white: #fff;
    --thumb: #edf2f4;
  }
  
  
  
  body {
    font: 16px / 24px "Rubik", sans-serif;
    color: var(--black);
    background: url("data:image/jpeg;base64,");
    max-width:100%;
  }
  
  
  h2 {
    font-size: 32px;
    margin-bottom: 1em;
  }
  
  .cards {
    display: flex;
    padding: 25px 0px;
    list-style: none;
    overflow-x: scroll;
    scroll-snap-type: x mandatory;
    max-width:100%;
  }
  
  .card {
    display: flex;
    flex-direction: column;
    flex: 0 0 100%;
    padding: 20px;
    background: var(--white);
    border-radius: 12px;
    box-shadow: 0 5px 15px rgba(0, 0, 0, 15%);
    scroll-snap-align: start;
    transition: all 0.2s;
    max-width:100%;
  }
  
  .btn-primary{
    background-color:grey;
    color:grey;
    border-color:grey;
  }
  .card:hover {
    color: var(--white);
    background: var(--grey);
  }
  
  .card .card-title {
    font-size: 20px;
    text-align:center;
  }
  
  .card .card-content {
    margin: 20px 0;
    max-width: 100%;
    text-align:center;
  }
  
  
  .card .card-link-wrapper {
    margin-top: auto;
  }
  
  .card .card-link {
    display: inline-block;
    text-decoration: none;
    color: white;
    background: var(--red);
    padding: 6px 12px;
    border-radius: 8px;
    transition: background 0.2s;
  }
  
  .card:hover .card-link {
    background: var(--darkred);
  }
  
  .cards::-webkit-scrollbar {
    height: 12px;
    
  }
  
  .cards::-webkit-scrollbar-thumb,
  .cards::-webkit-scrollbar-track {
    border-radius: 50px;
    
  }
  
  .cards::-webkit-scrollbar-thumb {
    background: var(--darkred);
    
  }
  
  .cards::-webkit-scrollbar-track {
    background: var(--thumb);
    
  }

  .modal-body{
    color:Black;
  }
  .modal-header{
    color:black;
  }
  
  button{
    margin-top:75px;
  }
  
  
  @media (min-width: 700px) {
    .card {
      flex-basis: calc(calc(100% / 3) - 20px);
      
    }
    .card:not(:last-child) {
      margin-right: 30px;
      
    }
  }
  
  
  @media (min-width: 1100px) {
    .card {
      flex-basis: calc(25% - 30px);
      
    }
    
    .card:not(:last-child) {
      margin-right: 40px;
      
    }
  }
  
  
  h2{
    margin-top:20px;
    height:100px;
    color:white;
    border:1px solid black;
    background-color:rgb(255, 47, 71);
    text-align:center;
    padding:25px;
  }
  
  img{
    padding-left:54px;
    width:350px;
    height:150px;
  }
</style>
